import requestsimport credentialsimport App_shopifyfrom App_shopify import get_draft_orders,get_customersimport pymysqlimport scheduleimport timefrom MySQL_class import MySQLimport sysdb = MySQL(host=credentials.ENDPOINT,                      user=credentials.USER,                      password=credentials.PASSWORD2)db.execute("CREATE DATABASE IF NOT EXISTS mysqldb")db.execute("USE mysqldb")db.execute("CREATE TABLE IF NOT EXISTS mysqldb.customers(first_name VARCHAR(50),last_name VARCHAR(50),email VARCHAR(50) NOT NULL UNIQUE)")#db.execute("CREATE TABLE IF NOT EXISTS mysqldb.orders(email VARCHAR(50),paid_price VARCHAR(10))")def obtain_customers():    customers_json = get_customers()        for customer in range(len(customers_json['customers'])):        first_name = customers_json['customers'][customer]['first_name']        last_name = customers_json['customers'][customer]['last_name']        email = customers_json['customers'][customer]['email']        exec_string = "INSERT into mysqldb.customers(first_name,last_name,email) VALUES ('"+first_name+"','"+last_name+"','"+email+"') ON DUPLICATE KEY UPDATE first_name = '"+first_name+"', last_name='"+last_name+"';"        db.execute(exec_string)    fetch = db.fetch("SELECT * FROM mysqldb.customers;")    return fetchfetch = obtain_customers()def run_app():     try:        new_customers = get_customers()        fetch = db.fetch("SELECT * FROM mysqldb.customers;")        for customer in range(len(new_customers['customers'])):                        if new_customers['customers'][customer] not in fetch:                first_name = new_customers['customers'][customer]['first_name']                last_name = new_customers['customers'][customer]['last_name']                email = new_customers['customers'][customer]['email']                exec_string = "INSERT IGNORE into mysqldb.customers(first_name,last_name,email) VALUES ('"+first_name+"','"+last_name+"','"+email+"');"                db.execute(exec_string)                fetch.append([first_name,last_name,email])                               except pymysql.err.IntegrityError as e:        passschedule.every(60).seconds.do(run_app)while True:    schedule.run_pending()    time.sleep(1)